/* eslint-disable max-len */
import React, { CSSProperties, useState } from 'react';
import useInstance, {
  DocumentViewer,
  DocumentViewerSimpleDisplay,
} from '../../www/lib';
import { getInitialWvOptions } from './utils';
import Modal from './components/Modal';
import Drawer from './components/Drawer';
import PopoverAddText from './components/PopoverAddText';
import Annotate from './components/Annotate';
import CardButton from './CardButton';
import { Tabs, TabList, TabPanels, Tab, TabPanel, Text, UnorderedList, ListItem } from '@chakra-ui/react';

const dataSimpleDisplay = {
  imageUrl: 'data:image/png;base64,',
  mode: 'Simple Display',
  bestFor: 'Simple use cases such as document display and text search',
};

const dataFullUI = {
  imageUrl: 'data:image/png;base64,',
  mode: 'Full UI',
  bestFor: 'Demanding use cases such as annotate, edit and sign document',
};

const leftBlockStyles = {
  width: '320px',
  minWidth: '320px',
  borderTop: '1px solid #d3d3d3',
  position: 'relative',
  height: 'calc(100vh - 50px)',
  overflowY: 'scroll'
};

const rightBlockStyles = {
  padding: '40px',
  background: '#eaeaea',
  borderLeft: '1px solid #d3d3d3',
  borderTop: '1px solid #d3d3d3',
  bottom: 0,
  minWidth: '670px',
  overflowY: 'hidden',
  top: '130px',
  transition: 'all 200ms',
  width: 'calc(100vw - 400px)',
  zIndex: 1,
};


function Display() {
  const { setInstance } = useInstance();
  const [fullUi, setSFullUi] = useState(false);

  function simpleDisplay() {
    if (fullUi) {
      setInstance();
      setSFullUi(false);
    }
  }

  function showFullUi() {
    if (!fullUi) {
      setInstance();
      setSFullUi(true);
    }
  }

  return (
    <>
      <div style={leftBlockStyles as CSSProperties}>
        <Tabs>
          <TabList m={2} pt="15" justifyContent="space-around">
            <Tab padding={2} _selected={{ color: 'white', bg: 'cyan.600' }}>Integration Options</Tab>
            <Tab padding={2} _selected={{ color: 'white', bg: 'cyan.600' }}>More Examples</Tab>
          </TabList>
          <TabPanels>
            <TabPanel p="2" mr="2">
              <CardButton data={dataSimpleDisplay} onClick={simpleDisplay}/>
              <CardButton data={dataFullUI} onClick={showFullUi}/>
            </TabPanel>
            <TabPanel>
              <Text fontSize="md" m="2">
        The React components {`<DocumentViewer />`} and {`<DocumentViewerSimpleDisplay />`} not only gives you a document viewer, but also provides you controls over the
        WebViewer instance everywhere in your app.
                <UnorderedList marginInlineStart={'1.3em'}>
                  <ListItem>
                    <Text fontSize="md">
            Want to handle your document inside you cool modal component? No
            problem!
                    </Text>
                  </ListItem>
                  <ListItem>
                    <Text fontSize="md">
            Want to search texts inside a custom drawer? You got it!
                    </Text>
                  </ListItem>
                </UnorderedList>
            The functionalities below are just a few basic examples of what you can do with it:
              </Text>
              <Modal />
              <Drawer />
              <PopoverAddText />
              <Annotate />
            </TabPanel>
          </TabPanels>
        </Tabs>
      </div>
      <div style={rightBlockStyles as CSSProperties}>
        {fullUi ? (
          <DocumentViewer {...getInitialWvOptions()} />
        ) : (
          <DocumentViewerSimpleDisplay {...getInitialWvOptions()} />
        )}
      </div>
    </>
  );
}

export default Display;
